      className,
      children,
      containerProps: rootProps,
      motionProps,
      ...rest
    } = props

    const { getDialogProps, getDialogContainerProps } = useModalContext()

    const dialogProps = getDialogProps(rest, ref) as any
    const containerProps = getDialogContainerProps(rootProps)

